package com.glomex.contentsdk

import java.util.*

/** Test utilities. */
object Utils {
    /**
     * Generates [List] of objects generated by [factory].
     */
    fun <T> randomList(
            capacity: Int = Random(System.currentTimeMillis()).nextInt(10),
            factory: () -> T): List<T> {
        val values = mutableListOf<T>()
        (0..capacity).forEach {
            values.add(factory())
        }
        return values.toList()
    }
}